declare const operators: {
    eq: string;
    ne: string;
    gt: string;
    gte: string;
    lt: string;
    lte: string;
    in: string;
    nin: string;
    like: string;
    nlike: string;
    sw: string;
    ew: string;
    is: string;
    isn: string;
};

type Operator = keyof typeof operators;
type Where = {
    and: (field: string, operator: Operator, value: any) => Where;
    or: (orConditions: [string, Operator, any][]) => Where;
    build: () => {
        sql: string;
        values: any[];
    };
    page: (page: number) => Where;
    perPage: (perPage: number) => Where;
    pageValue: number;
    perPageValue: number;
};

declare function where(str?: string): Where;

export { type Operator, type Where, where };
